import React, { useState }  from "react";
import TableRow from './TableRow'

function Home( {list} ) {
  let sortList = list
  const [ newList, setList ] = useState( )

  function sort( object ){
    const newList = list.sort( (a,b) => {return a[object] > b[object] ? 1 : -1; })
    setList(newList)
    sortList = newList
  }

  return (
    <div className="container">
        <table className="table table-hover">
            <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Name <button type="button"  object='last_name' onClick={sort} ></button></th>
                    <th scope="col" >Location<button type="button"  object='city' onClick={sort} ></button></th>
                </tr>
            </thead>
            <tbody>
                {Object.entries(sortList).map( ([key,value]) => <TableRow key={key} list={[Number(key)+1,value]} /> )}
            </tbody>
        </table>
    </div>
  );
}

export default Home;
